var link=document.querySelector(".btn-contacts");var popup=document.querySelector(".modal-feedback");var close=popup.querySelector(".modal-close");var form=popup.querySelector("form");var login=popup.querySelector("[name=name]");var email=popup.querySelector("[name=email]");var isStorageSupport=true;var storage="";try{storage=localStorage.getItem("name")}catch(err){isStorageSupport=false}link.addEventListener("click",function(a){a.preventDefault();popup.classList.add("modal-show");if(storage){login.value=storage;email.focus()}else{login.focus()}});close.addEventListener("click",function(a){a.preventDefault();popup.classList.remove("modal-show");popup.classList.remove("modal-error")});form.addEventListener("submit",function(a){if(!login.value||!email.value){a.preventDefault();popup.classList.remove("modal-error");popup.offsetWidth=popup.offsetWidth;popup.classList.add("modal-error")}else{if(isStorageSupport){localStorage.setItem("name",login.value)}}});window.addEventListener("keydown",function(a){if(a.keyCode===27){a.preventDefault();if(popup.classList.contains("modal-show")){popup.classList.remove("modal-show");popup.classList.remove("modal-error")}}});var slideIndex=1;showSlides(slideIndex);function plusSlide(){showSlides(slideIndex+=1)}function minusSlide(){showSlides(slideIndex-=1)}function currentSlide(a){showSlides(slideIndex=a)}function showSlides(d){var a;var b=document.getElementsByClassName("slide");var c=document.getElementsByClassName("dot");if(d>b.length){slideIndex=1}if(d<1){slideIndex=b.length}for(a=0;a<b.length;a++){b[a].style.display="none"}for(a=0;a<c.length;a++){c[a].className=c[a].className.replace("dot-active","")}b[slideIndex-1].style.display="block";c[slideIndex-1].className+="dot-active"};